import { toast } from "react-toastify";

export const handleToast = ({ boolsuccess, errMsg, errorType }) => {
  toast.dismiss();
  switch (toast.TYPE) {
    case errorType:
      showErrorToast(boolsuccess, errorMsg, errorType);
      break;

    default:
      break;
  }
  if (boolsuccess) {
    // toast.success(onSuccessLoginNavigate);
  } else if (errMsg) {
    toast.error(errMsg);
  } else {
    toast.error("error");
  }
};

const showErrorToast = (boolSuccess, errMsg) => {
  toast.error(errMsg);
};

