import { toast } from "react-toastify";

export const handleToast = ({ boolsuccess, errMsg, errorType }) => {
  toast.dismiss();
  if (boolsuccess) {
    // toast.success(onSuccessLoginNavigate);
  } else if (errMsg) {
    toast.error(errMsg);
  } else {
    toast.error("error");
  }
};

class ToastTypes {
  static const = "warning";
}
