import { API, cPrint } from "../../backend";

// Category Calls
export const createCategory = async (userId, token, categoryName) => {
  return await fetch(`${API}/category/create/${userId}`, {
    method: "POST",
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
      Authorization: `Bearer ${token}`,
    },
    body: JSON.stringify(categoryName),
  })
    .then((response) => {
      return response.json();
    })
    .catch((err) => {
      cPrint(`error: ${err}`);
    });
};

export const getAllCategory = async () => {
  return await fetch(`${API}/categories`, {
    method: "GET",
    headers: {
      Accept: "application/json",
    },
  })
    .then((response) => {
      return response.json();
    })
    .catch((err) => {
      cPrint(`error: ${err}`);
    });
};

export const updateCategory = async ({ name, categId, userId, token }) => {
  cPrint(JSON.stringify(name.name));
  //   cPrint(name);
  return await fetch(`${API}/category/${categId}/${userId}`, {
    method: "PUT",
    headers: {
      Accept: "application/json",
      Authorization: `Bearer ${token}`,
    },
    body: { name: name },
  })
    .then((response) => {
      return response.json();
    })
    .catch((err) => {
      cPrint(`error: ${err}`);
    });
};

// TODO:  Product Calls
