import React, { useEffect, useState } from "react";
import { getAllCategory } from "./helper/adminapicall";
import { handleToast } from "../reusables/HandleToast";
import { ToastContainer } from "react-toastify";
import Base from "../core/Base";
import { cPrint } from "../backend";

const ManageCategory = () => {
  const [categories, setCategories] = useState();
  const [updateCategName, setUpdateCategName] = useState("");
  const [categoryEdit, setCategoryEdit] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      getAllCategory()
        .then((data) => {
          if (data.error) {
            handleToast(false, data.error, "error");
            setLoading(false);
          } else if (typeof data == typeof [] && data) {
            setCategories(data);
            handleToast(true, "data fetched", "success");
            setLoading(false);
          }
        })
        .catch((err) => {
          setLoading(false);
          handleToast(false, err, "error");
        });
    }, 2000);

    // return () => {
    //   cleanup;
    // };
  }, []);

  // Actions

  const selectCategoryToEdit = (value) => {
    setCategoryEdit(value);
    setUpdateCategName(value.name);
  };

  const onSubmit = (event) => {
    event.preventDefault();
  };

  const handleChange = (event) => {
    setUpdateCategName(event.target.value);
    // let updatedCategry = { name: event.target.value, ...categoryEdit };
    // setCategoryEdit(updatedCategry);
  };

  // Sub UI

  const showAllCategories = () => {
    return (
      <div>
        {categories &&
          categories.map((value) => (
            <div className="row">
              <div
                className="col-md-8 card btn btn-sm btn-outline-warning 
              bg-opacity-25 bg-body text-white text-center m-2"
                onClick={() => selectCategoryToEdit(value)}
              >
                <h2 key={value._id}>{value.name}</h2>
              </div>
              <div
                className="col-md-2 card bg-danger btn-outline-light 
              btn-sm m-2 my-3 text-white text-center justify-content-center"
              >
                Delete
              </div>
            </div>
          ))}
      </div>
    );
  };

  const editCategoryForm = () => {
    return (
      <div className="card">
        <h5 className="card-header container text-center">
          {categoryEdit.name ?? "Category Name"}
        </h5>
        <div className="container">
          <form action="">
            <div className="form-group">
              <input
                type="text"
                className="form-control my-2"
                readOnly
                placeholder="For ex. Kamlesh"
                value={categoryEdit._id}
              />
              <p className="lead">Enter the category name</p>
              <input
                type="text"
                className="form-control my-2"
                onChange={handleChange}
                name="name"
                value={updateCategName}
                autoFocus
                required={true}
                placeholder="For ex. Mobile"
              />
              <input
                type="text"
                className="form-control my-2"
                readOnly
                placeholder="Created At"
                value={categoryEdit.createdAt}
              />
              <input
                type="text"
                className="form-control my-2"
                readOnly
                placeholder="Updated At"
                value={categoryEdit.updatedAt}
              />
              <button
                className="btn btn-outline-success my-2"
                onClick={onSubmit}
              >
                Update Category
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  };

  const showLoading = () => {
    return (
      loading && (
        <div className="alert alert-info">
          <h2>Loading.....</h2>
        </div>
      )
    );
  };

  // main UI
  return (
    <Base
      title="Manage category here"
      description="Add a new category for new tshirts"
      className="container bg-success p-4"
    >
      <ToastContainer autoClose={3000} limit={3} />
      {showLoading()}
      <div className="row rounded align-items-center">
        <div className="col-4">{showAllCategories()}</div>
        <div className="col-8">{categoryEdit && editCategoryForm()}</div>
      </div>
    </Base>
  );
};

export default ManageCategory;
