import React, { useEffect, useState } from "react";
import Base from "../core/Base";
import { Link } from "react-router-dom";
import { createaProduct, getAllCategory } from "./helper/adminapicall";
import { isAuthenticated } from "../auth/helper";
import { handleToast } from "../reusables/HandleToast";

const AddProduct = () => {
  const { user, authToken } = isAuthenticated();

  const [values, setValues] = useState({
    name: "",
    description: "",
    price: "",
    stock: "",
    photo: "",
    categories: [],
    category: "",
    loading: false,
    error: "",
    createdProduct: "",
    getaRedirect: false,
    formData: "",
  });

  const {
    name,
    description,
    price,
    stock,
    photo,
    categories,
    category,
    loading,
    error,
    createdProduct,
    getaRedirect,
    formData,
  } = values;

  const preload = async () => {
    await getAllCategory().then((data) => {
      if (data.error) {
        setValues({ ...values, error: data.error });
        handleToast(false, data.error, "error");
      } else {
        setValues({ ...values, categories: data, formData: new FormData() });
        // handleToast(false, `${createdProduct} created successfully`, "success");
      }
    });
  };

  useEffect(() => {
    preload();
  }, []);

  const onSubmit = (event) => {
    console.log("inn");
    event.preventDefault();
    setValues({ ...values, error: "", loading: true });
    createaProduct(user._id, authToken, formData).then((data) => {
      if (data.error) {
        setValues({ ...values, error: data.error });
        handleToast(false, data.error, "error");
      } else {
        setValues({
          ...values,
          name: "",
          description: "",
          price: "",
          photo: "",
          stock: "",
          loading: false,
          createdProduct: data.name,
        });
        handleToast(true, `${data.name} created successfully`, "success");
      }
    });
  };

  const handleChange = (name) => (event) => {
    const value = name === "photo" ? event.target.files[0] : event.target.value;
    formData.set(name, value);
    setValues({ ...values, [name]: value });
  };

  const createProductForm = () => (
    <form>
      <span>Post photo</span>
      <div className="form-group col">
        {/* <label className="btn btn-block btn-success mt-2"> */}
        <input
          onChange={handleChange("photo")}
          type="file"
          name="photo"
          className="form-control"
          accept="image"
          placeholder="choose a file"
        />
        {/* </label> */}
      </div>
      <div className="form-group mt-2">
        <input
          onChange={handleChange("name")}
          name="name"
          className="form-control"
          placeholder="Name"
          value={name}
        />
      </div>
      <div className="form-group mt-2">
        <textarea
          onChange={handleChange("description")}
          name="description"
          className="form-control"
          placeholder="Description"
          value={description}
        />
      </div>
      <div className="form-group mt-2">
        <input
          onChange={handleChange("price")}
          type="number"
          name="price"
          className="form-control"
          placeholder="Price"
          value={price}
        />
      </div>
      <div className="form-group mt-2">
        <select
          onChange={handleChange("category")}
          className="form-control"
          placeholder="Category"
          name="category"
        >
          <option>Select</option>
          {categories &&
            categories.map((categ, index) => (
              <option key={index} value={categ._id}>
                {categ.name}
              </option>
            ))}
        </select>
      </div>
      <div className="form-group mt-2">
        <input
          onChange={handleChange("stock")}
          type="number"
          className="form-control"
          placeholder="Quantity"
          name="quantity"
          value={stock}
        />
      </div>

      <button
        type="submit"
        onClick={onSubmit}
        className="btn btn-outline-success my-3"
      >
        Create Product
      </button>
    </form>
  );

  return (
    <Base
      title="Add a product here!"
      description="Welcome to product creation section"
      className="container bg-info p-4"
    >
      <Link to="/admin/dashboard" className="btn btn-md btn-dark mb-3">
        Admin Home
      </Link>
      <div className="row bg-dark text-white rounded">
        <div className="col-md-8 offset-md-2">{createProductForm()}</div>
      </div>
    </Base>
  );
};

export default AddProduct;
