import { toast } from "react-toastify";

export const handleToast = ({ boolsuccess, errMsg, errorType }) => {
  toast.dismiss();
  if (boolsuccess) {
    errorType(errMsg);
  } else if (errMsg) {
    errorType(errMsg);
  } else {
    errorType("error");
  }
};

